INSERT INTO all_news VALUES ('53', 'https://edition.cnn.com/2024/09/17/Tv/video/amanpour-cindy-mccain-sudan-world-food-programme', '@ChefGov_ma', 'https://vod-media-aka.warnermediacdn.com/cnn/v2/clips/2024-09/1647738-bfbc55c96fff420f80b521e851aeada3/mp4/mediacity-ingest-request-FuRH1ZLnjhl-1647738-1920x1080_8000k.mp4', 'https://media.cnn.com/api/v1/images/stellar/prod/cindy-mccain.jpg?c=original&q=h_720,w_1280,c_fill', '\'We need to get in now\': Cindy McCain urges access to Sudan for the World Food Programme', '“我们现在就得进去”: 辛迪 · 麦凯恩敦促世界粮食计划署进入苏丹', 'Bianna Golodryga speaks with Cindy McCain, executive director of the World Food Programme, about the rising crisis of famine conditions in Sudan, as the ongoing civil war causes a humanitarian disaster.', 'Bianna Golodryga 与世界粮食计划署执行主任 Cindy McCain 就苏丹日益严重的饥荒危机进行了交谈，目前正在进行的内战造成了一场人道主义灾难。', 'CNN');
